mapper.select:
SELECT board_seq AS boardSeq, type_seq AS typeSeq, member_idx AS memberIdx, member_id AS memberId, member_nick AS memberNick, title, hits, date_format(create_date, '%Y-%m-%d %H:%i:%s') AS createDate FROM board WHERE type_seq = #{typeSeq}
AND ( title LIKE CONCAT('%', #{searchText}, '%') OR content LIKE CONCAT('%', #{searchText}, '%') )
AND title LIKE CONCAT('%', #{searchText}, '%')
AND content LIKE CONCAT('%', #{searchText}, '%')
ORDER BY ${sidx} ${sord} LIMIT ${start}, ${rows}

mapper.update:
UPDATE board
   SET hits = hits + 1
 WHERE type_seq = #{typeSeq}
   AND board_seq = #{boardSeq}

mapper.delete:
DELETE FROM board
 WHERE type_seq = #{typeSeq}
   AND board_seq = #{boardSeq}

mapper.update:
UPDATE board
   SET has_file = 0
 WHERE type_seq = #{typeSeq}
   AND board_seq = #{boardSeq}
